import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup,  Validators } from '@angular/forms';
import { AuthService } from 'src/app/services/auth.service';
@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.scss']
})
export class SignupComponent implements OnInit {
  signupForm!: FormGroup
name!: FormControl<any>;
email!: FormControl<any>;
password!: FormControl<any>;
  constructor(private fb: FormBuilder, private auth: AuthService){

  }

  ngOnInit(): void {
    this.signupForm = this.fb.group({
      name: [null, Validators.required],
      email: [null, Validators.required, Validators.email],
      password:[null, Validators.required, Validators.maxLength(8)]
    })

  }
  submit(data: any){
      this.auth.userSignUp(data);  
      console.log(data);

    }
  }



<div class="container-fluid">
    <h1 class="text-center">SignUp Form</h1>
    <form [formGroup]="signupForm" (ngSubmit)="submit(signupForm.value)" class="signup-form">       

        <div fxLayout="row" fxLayout.xs="column" class="mt-4">
          <div fxFlex="100%" fxFlex.sm="100%" fxFlex.xs="100%">
              <mat-label>Name</mat-label>
              <mat-form-field class="w-100" appearance="outline">
                <input class="form-control" matInput [formControl]="name" placeholder="Enter your name">    
              </mat-form-field>
          </div>
        </div>    
        <div fxLayout="row" fxLayout.xs="column">
          <div fxFlex="100%" fxFlex.sm="100%" fxFlex.xs="100%">
              <mat-label>Email</mat-label>
              <mat-form-field class="w-100" appearance="outline">
                <input class="form-control" matInput [formControl]="email" placeholder="Enter your email">       
              </mat-form-field>
          </div>
        </div>
        <div fxLayout="row" fxLayout.xs="column">
          <div fxFlex="100%" fxFlex.sm="100%" fxFlex.xs="100%">
              <mat-label>Password</mat-label>
              <mat-form-field class="w-100 remove-padding" appearance="outline">
                  <input class="form-control" type="password" matInput placeholder="Enter your password"  [formControl]="password">
              <!-- <mat-error *ngIf="signupForm.get('password')?.errors?.['required']">Password is required</mat-error> -->
              </mat-form-field>
          </div>
        </div>
      <!-- <div class="signup-btn">
        <button class="btn btn-primary w-100" type="submit">Sign Up</button>
      </div> -->
      <div>
        <button type="submit" class="mat-button w-100 mat-button-primary mt-4">Sign Up</button>
    </div>
    </form>










</div>
